import { emptyChatImage } from "../../../constants/data";
import { Box, Typography, styled } from '@mui/material';

const Component = styled(Box)`
    background: #f8f9fa;
    padding: 30px 0;
    text-align: center;
    height: 100%;
    width: 100%; /* Ensures the component takes the full width */
    display: flex;
    justify-content: center; /* Centers the content horizontally */
    align-items: center; /* Centers the content vertically */
`;

const Container = styled(Box)`
    padding: 0 20px; /* Reduce padding for responsiveness */
    max-width: 800px; /* Limits container width for larger screens */
    width: 100%; /* Makes it responsive */
`;

const Image = styled('img')({
    width: '100%',
    maxWidth: 400,
    marginTop: 50,
});

const EmptyChat = () => {
    return (
        <Component> 
            <Container>
                <Image src={emptyChatImage} alt="empty chat" />
                <Typography variant="h5" gutterBottom>WhatsApp Web</Typography>
                <Typography variant="body1">Now send and receive messages without keeping your phone online.</Typography>
                <Typography variant="body2">Use WhatsApp on up to 4 linked devices and 1 phone at the same time.</Typography>
            </Container>
        </Component>
    );
};

export default EmptyChat;
