import { useContext } from "react";
import { Box, styled } from '@mui/material'

import { AccountContext } from "../../../context/AccountProvier";
import { Chat as Messageicon } from '@mui/icons-material'; // Correct import for the Chat icon
import { MoreVert } from '@mui/icons-material'; // Correct import for the MoreVert icon

const Component = styled(Box)`
    height: 44px;
    background:#ededed;
    padding: 8px 16px;
    display: flex;
 align-items: center;
`

const Wrapper = styled(Box)`
    margin-left: auto;
    & > * 
`

const Image = styled('img')({
    height: 40,
    width: 40,
    borderRadius: '50%'
})


const Header = () => {

    const { account } = useContext(AccountContext);

    return (
        <>
            <Component>
                <Image src={account.picture} alt="dp" />
                <Wrapper>
                    <Messageicon />
                    <MoreVert />
                </Wrapper>
            </Component>
        </>

    )
}
export default Header;
