import { useState } from 'react';
import { MoreVert } from "@mui/icons-material";
import { Menu, MenuItem } from "@mui/material";

const MenuOption = styled(MenuItem)`
    font-size: 14px;
    padding: 15px 60px 5px 24px;
    color: #4A4A4A4A
`

const HeaderMenu = () => {
    const [anchorEl, setAnchorEl] = useState(null);

    const handleClose = () => {
        setAnchorEl(null);
    }

    const handleClick = (e) => {
        setAnchorEl(e.currentTarget);
    }

    return (
        <>
            <MoreVert onClick={handleClick} />



            <Menu
                anchorEl={anchorEl}  // Fixed typo here
                keepMounted
                open={Boolean(anchorEl)}  // Use Boolean to convert anchorEl to boolean
                onClose={handleClose}
                getContentAnchorEl={null}  // This prop is deprecated; no need to use it
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'center'
                }}
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'right'
                }}
            >
                <MenuItem onClick={handleClose}>Profile</MenuItem>

            </Menu>
        </>
    )
}

export default HeaderMenu;
